import { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';


import Cart from './components/Cart/Cart';
import Layout from './components/Layout/Layout';
import Products from './components/Shop/Products';
// import { cartActions } from './store/cartStore';
import Notification from './components/UI/Notification';
import { sendCartData } from './store/cart-slice';

let isInitial = true

function App() {
  const showCart = useSelector(state => state.cartShown.isCartShown)
  const notification = useSelector(state => state.cartShown.notification)
  const cart = useSelector(state => state.cartItems)
  const dispatch = useDispatch()

  useEffect(()=>{
    // const sendCartData = async ()=> {
    //   dispatch(cartActions.showNotification({
    //     status: 'pending',
    //     title: 'Sending...',
    //     message: 'Sending cart data!'
    //   }))

    //   const res = await fetch('https://react-http-d5ecc-default-rtdb.firebaseio.com/cart.json', {
    //     method: 'PUT',
    //     body: JSON.stringify(cart)
    //   })

    //   if(!res.ok) {
    //     throw new Error('Sending cart data failed')
    //   }

    //   dispatch(cartActions.showNotification({
    //     status: 'success',
    //     title: 'Success',
    //     message: 'Sent cart data successfully!'
    //   }))

    // }

    if(isInitial) {
      isInitial = false
      return
    }

    dispatch(sendCartData(cart))

    // sendCartData().catch((error)=>{
    //   dispatch(cartActions.showNotification({
    //     status: 'error',
    //     title: 'Error...',
    //     message: 'Sending cart data failed!'
    //   }))
    // })
  }, [cart, dispatch]);


  return (
    <>
    {notification && 
    <Notification 
      status={notification.status} 
      title={notification.title} 
      message={notification.message} 
    />}
      <Layout>
        {showCart && <Cart />}
        <Products />
      </Layout>

    </>
  );
}

export default App;
